void SF_ProviderLog_Provider::ChangeState::handler(solarflare::SystemElement&,
                                                   unsigned idx)
{
    solarflare::Logger *log = solarflare::Logger::getKnownLogs()[idx];

    switch (reqState)
    {
        case SF_ProviderLog::_RequestedState::enum_Enabled:
            log->enable(true);
            break;
        case SF_ProviderLog::_RequestedState::enum_Disabled:
            log->enable(false);
            break;
        case SF_ProviderLog::_RequestedState::enum_Reset:
            if (log->isEnabled())
            {
                log->enable(false);
                log->enable(true);
            }
            break;
        default:
            // cannot happen
            break;
    }

    solarflare::System::saveLogReqState(log->description(), reqState);
}


